import Loadable from "react-loadable"

// containers
import Home from "../client/components/App"
import MovieDetail from "../client/containers/moviedetail/index"
import NotFound from "../client/containers/errors/NotFound"

// routes
export default [
  {
    routes: [
      {
        path: "/",
        exact: true,
        component: Home
      },
      {
        path: "/movie/:id",
        exact: true,
        component: MovieDetail
      },
      // default page (return 404)
      {
        path: "*",
        state: { fullscreen: true },
        exact: true,
        component: NotFound
      }
    ]
  }
]
