import React from 'react';
import './css/Card.css';

class Card extends React.Component {

  handleMovieCard = id => {
    console.log('ID: ', id)
  }

  render() {
    return (
      <div className="card"

      >
        <figure className="card_content">         
          <figcaption>
            <h3><a onClick={() => this.handleMovieCard(this.props.id)} >{this.props.title} ({this.props.release_date})</a></h3>
            <hr />
            <div className="description" 
              dangerouslySetInnerHTML={{ __html: this.props.description }} 
            />
            <div className="tags">
              <div>Producer: {this.props.producer}</div>
              <div>Director: {this.props.director}</div>
              <div>Score: {this.props.rt_score}</div>
            </div>
          </figcaption>
        </figure>
      </div>
    );
  }
}

export default Card;
