import React, { Component } from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types'

import { getImage } from './store/reducers';

import './css/App.css';

class App extends Component {
  constructor(props, context) {
    super(props, context)
    this.state = {
      addressSearchvalue: '',
    }
  }

  static propTypes = {
    getFlickrImage: PropTypes.func.isRequired,
    cards: PropTypes.array,
    loading: PropTypes.bool,
    error: PropTypes.bool,
  }

  componentWillMount() {
    this.props.getFlickrImage();
  }

  searchHandleChange = e => {
    const value = e.target.value
    if(value.length >= 3) {
      this.setState({ addressSearchvalue: value })
    } else {
      this.setState({ addressSearchvalue: '' })
    }
  }

  searchHandleClick = e => {
    e.preventDefault()
    console.log('Value: ', this.state.addressSearchvalue)
  }

  render() {
    const { loading, error, cards } = this.props;

    return (
      <div className="App">
        <div className="wrapper">
          <div className="header">
            <input
              type="text"
              placeholder="Search"
              onChange={this.searchHandleChange}
              className="search"
            />
            <button className="search_button" onClick={this.searchHandleClick}>Search</button>
          </div>
          <div className="content flexWrap">
            {loading ? 'Loading...' : error ? 'OOPS!!! There was an error connection' : cards}
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  console.log('STATE: ', state)
  return {
    cards: state.cards,
    error: state.error,
    loading: state.loading
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    getFlickrImage: (url) => dispatch(getImage(url))
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(App);
