import React, { Component } from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types'

import { getImage } from './store/reducers';

import Card from './Card'
import './css/App.css';

class App extends Component {
  constructor(props, context) {
    super(props, context)
    this.state = {
      addressSearchvalue: '',
    }
  }

  static propTypes = {
    getFlickrImage: PropTypes.func.isRequired,
    imageData: PropTypes.array,
    loading: PropTypes.bool,
    error: PropTypes.bool,
  }

  componentWillMount() {
    this.props.getFlickrImage();
  }

  searchHandleChange = e => {
    const value = e.target.value
    if(value.length >= 3) {
      this.setState({ addressSearchvalue: value })
    } else {
      this.setState({ addressSearchvalue: '' })
    }
  }

  searchHandleClick = e => {
    e.preventDefault()
    const input = document.getElementById('search').value;
    console.log('Value: ', input)
  }

  render() {
    const { loading, error, imageData } = this.props;

    return (
      <div className="App">
        <div className="wrapper">
          <div className="header">
            <input
              type="text"
              placeholder="Search"
              onChange={this.searchHandleChange}
              id="search"
            />
            <button className="search_button" onClick={this.searchHandleClick}>Search</button>
          </div>
          <div className="content flexWrap">
            {loading ? 'Loading...' : error ? 'OOPS!!! There was an error connection' : (
              imageData
              .filter(data => new RegExp(addressSearchvalue, "i").test(data.title) || new RegExp(addressSearchvalue, "i").test(data.description) )
              .map((card, index) => (
                <Card
                  key={index}
                  author={card.author}
                  title={card.title}
                  description={card.description}
                  image={card.media.m}
                  tags={card.tags}
                />
              ))

            )}
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  console.log('STATE: ', state)
  return {
    imageData: state.imageData,
    error: state.error,
    loading: state.loading
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    getFlickrImage: (url) => dispatch(getImage(url))
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(App);
