import React, { Component } from 'react';
import { connect } from 'react-redux';
import { getImage } from './store/reducers';

import './css/App.css';

class App extends Component {

  componentWillMount() {
    const url = 'https://api.flickr.com/services/feeds/photos_public.gne?format=json';
    this.props.fetchData(url);

  }

  render() {
    console.log(this.props);
    const { loading, error, cards } = this.props;

    return (
      <div className="App">
        <div>
          <div className="header">
            <h2>
                Flickr Images
            </h2>
          </div>
          <div className="content flexWrap">
            {loading ? 'Loading...' : error ? 'OOPS!!! There was an error connection' : cards}
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    cards: state.cards,
    error: state.error,
    loading: state.loading
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    fetchData: (url) => dispatch(getImage(url))
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(App);
