import React from 'react';
import { combineReducers } from 'redux';
import axios from 'axios'

import Card from '../Card';


const GET_IMAGE_LOADING = 'GET_IMAGE_LOADING';
const GET_IMAGE_SUCCESS = 'GET_IMAGE_SUCCESS';
const GET_IMAGE_ERROR = 'GET_IMAGE_ERROR';

const initialState = {
  imageLists: [],
}


export function getImageLoading(bool) {
  return {
    type: GET_IMAGE_LOADING,
    loading: bool
  };
}

export function getImageSuccess(cards) {
  return {
    type: GET_IMAGE_SUCCESS,
    cards
  };
}

export function getImageError(bool) {
  return {
    type: GET_IMAGE_ERROR,
    error: bool
  };
}

export function getImage(url) {
  const api_path = `http://localhost:5000/api/getImage`

  return (dispatch) => {
    dispatch(getImageLoading(true));
    axios.post(api_path)
    .then(res => {
      return res.data;
    })
    .then((data) => {
      let cards = [];
      cards = data.items.map((card, index) => (
        <Card
          key={index}
          author={card.author}
          title={card.title}
          description={card.description}
          image={card.media.m}
          tags={card.tags}
        />
      ))

      dispatch(getImageSuccess(cards))
      dispatch(getImageLoading(false));
    })
    .catch(() => dispatch(getImageError(true)));
  };
}

export function error(state = false, action) {
  switch (action.type) {
    case GET_IMAGE_ERROR:
      return action.error;
    default:
      return state;
  }
}

export function loading(state = false, action) {
  switch (action.type) {
    case GET_IMAGE_LOADING:
      return action.loading;
    default:
      return state;
  }
}

export function cards(state = initialState, action) {
  console.log(state, action)
  switch (action.type) {
    case GET_IMAGE_SUCCESS:
      return action.cards;
    default:
      return state;
  }
}

export default combineReducers({
    error,
    loading,
    cards
});
