import React, { Component } from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types'

import { getImage } from './store/reducers';

import './css/App.css';

class App extends Component {
  static propTypes = {
    getFlickrImage: PropTypes.func.isRequired,
    cards: PropTypes.array,
    loading: PropTypes.bool,
    error: PropTypes.bool,
  }

  componentWillMount() {
    this.props.getFlickrImage();
  }

  render() {
    const { loading, error, cards } = this.props;

    return (
      <div className="App">
        <div>
          <div className="header">
            <input className="search"></input>
          </div>
          <div className="content flexWrap">
            {loading ? 'Loading...' : error ? 'OOPS!!! There was an error connection' : cards}
          </div>
        </div>
      </div>
    );
  }
}

const mapStateToProps = (state) => {
  return {
    cards: state.cards,
    error: state.error,
    loading: state.loading
  };
};

const mapDispatchToProps = (dispatch) => {
  return {
    getFlickrImage: (url) => dispatch(getImage(url))
  };
};

export default connect(mapStateToProps, mapDispatchToProps)(App);
